import React, { Component } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';


export const Register = () => {
    const [checked, setChecked] = React.useState([true, false]);
    
    const handleChangePublic = (event) => {
        setChecked([event.target.checked, checked[0]]);
    };

    const handleChangePrivate = (event) => {
        setChecked([checked[1], event.target.checked]);
    };

    return (
        <Box
            component="form"
            sx={{
                marginTop: 12,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                '& .MuiTextField-root': { m: 1, width: '25ch' },
            }}
            noValidate
            autoComplete="on"
        >
        <FormControl sx={{ width: '25ch' }}>
      
            <Grid item xs={12} sm={6}>
                <TextField
                    margin="dense"
                    helperText="Please enter your username"
                    id="demo-helper-text-aligned"
                    label="Username"
                    defaultValue=""
                    style={{ width: 425 }}
                />
            </Grid>
            <Grid item xs={12} sm={6}>
                <TextField
                    margin="dense"
                    helperText="Please enter your password"
                    id="utlined-adornment-password"
                    label="Password"
                    defaultValue=""
                    type="password"
                    style={{ width: 425 }}
                />
            </Grid>
            <Grid item xs={12} sm={6}>
                <TextField
                    margin="dense"
                    helperText="Please reenter your password"
                    id="utlined-adornment-password"
                    label="Reenter password"
                    defaultValue=""
                    type="password"
                    style={{ width: 425 }}
                />
            </Grid>
            <Grid item xs={12} sm={6}>
                <FormControlLabel
                    control={
                        <Checkbox checked={checked[0]} onChange={handleChangePublic} name="publicState" />
                    }
                    label="Public"
                />
                <FormControlLabel
                    control={
                        <Checkbox checked={checked[1]} onChange={handleChangePrivate} name="privateState" />
                    }
                    label="Private"
                />
            </Grid>
            <Grid item xs={12} sm={6}>
                <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    sx={{ mt: 1, mb: 1 }}
                    style={{ width: 447 }}
                >
                    Register
                </Button>
            </Grid>
            <Grid item xs={12} sm={6}>
                <Button
                    type="submit"
                    href="#"
                    fullWidth
                    variant="outlined"
                    sx={{ mt: 1, mb: 1 }}
                    style={{ width: 447 }}
                >
                    Log In
                </Button>
            </Grid>
            </FormControl>
        </Box>
    );
}

//export default Login
